import React, { useState, useEffect } from 'react';
import './CompetitionScreen.css';

export default function CompetitionScreen({ character, onBack }) {
  const [competitionState, setCompetitionState] = useState('intro'); // intro, competing, results
  const [competitors, setCompetitors] = useState([]);
  const [playerRank, setPlayerRank] = useState(0);
  const [prize, setPrize] = useState('');

  useEffect(() => {
    // AI Í≤ΩÏüÅÏûêÎì§ ÏÉùÏÑ±
    const generateCompetitors = () => {
      const names = ['ÍπÄÏ≤†Ïàò', 'Ïù¥ÏòÅÌù¨', 'Î∞ïÎØºÏàò', 'ÏµúÏßÄÏùÄ', 'Ï†ïÌÉúÌò∏', 'ÌïúÏÜåÏòÅ', 'Ïú§ÎåÄÏÑ±', 'ÏûÑÏàòÏßÑ'];
      const totalStats = character.strength + character.endurance + character.agility;
      
      const aiCompetitors = names.map((name, index) => ({
        id: index + 1,
        name,
        totalStats: Math.max(50, totalStats + (Math.random() - 0.5) * 40),
        bodyType: totalStats >= 180 ? 'elite' : 
                 totalStats >= 140 ? 'advanced' : 
                 totalStats >= 100 ? 'intermediate' : 'beginner'
      }));

      // ÌîåÎ†àÏù¥Ïñ¥ Ï∂îÍ∞Ä
      const allCompetitors = [
        ...aiCompetitors,
        {
          id: 0,
          name: 'ÎÇò',
          totalStats,
          bodyType: character.bodyType,
          isPlayer: true
        }
      ];

      // Ï¥ù Îä•Î†•ÏπòÎ°ú Ï†ïÎ†¨
      const sortedCompetitors = allCompetitors.sort((a, b) => b.totalStats - a.totalStats);
      
      setCompetitors(sortedCompetitors);
      
      // ÌîåÎ†àÏù¥Ïñ¥ ÏàúÏúÑ Ï∞æÍ∏∞
      const playerIndex = sortedCompetitors.findIndex(comp => comp.isPlayer);
      setPlayerRank(playerIndex + 1);
      
      // ÏÉÅÍ∏à/ÏÉÅÌíà Í≤∞Ï†ï
      if (playerIndex === 0) setPrize('ü•á Í∏àÎ©îÎã¨ + 1000ÏΩîÏù∏');
      else if (playerIndex === 1) setPrize('ü•à ÏùÄÎ©îÎã¨ + 500ÏΩîÏù∏');
      else if (playerIndex === 2) setPrize('ü•â ÎèôÎ©îÎã¨ + 300ÏΩîÏù∏');
      else if (playerIndex <= 5) setPrize('üèÖ ÏûÖÏÉÅ + 100ÏΩîÏù∏');
      else setPrize('üéñÔ∏è Ï∞∏Í∞ÄÏÉÅ + 50ÏΩîÏù∏');
    };

    generateCompetitors();
  }, [character]);

  const startCompetition = () => {
    setCompetitionState('competing');
    
    // 3Ï¥à ÌõÑ Í≤∞Í≥º ÌëúÏãú
    setTimeout(() => {
      setCompetitionState('results');
    }, 3000);
  };

  const renderIntroScreen = () => (
    <div className="competition-intro fade-in">
      <h2 className="competition-title">üèÜ ÌÅ¨Î°úÏä§Ìïè ÎåÄÌöå</h2>
      <div className="competition-description">
        <p>4Ï£ºÍ∞ÑÏùò ÌõàÎ†®Ïù¥ ÎÅùÎÇ¨ÏäµÎãàÎã§!</p>
        <p>Ïù¥Ï†ú Îã§Î•∏ ÏÑ†ÏàòÎì§Í≥º Í≤ΩÏüÅÌï† ÏãúÍ∞ÑÏûÖÎãàÎã§.</p>
        <p>ÎãπÏã†Ïùò Ï¥ù Îä•Î†•Ïπò: <span className="highlight">{character.strength + character.endurance + character.agility}</span></p>
      </div>
      
      <div className="player-preview">
        <h3>Ï∞∏Í∞ÄÏûê Ï†ïÎ≥¥</h3>
        <div className="player-card">
          <div className="player-name">ÎÇò (Lv.{character.level})</div>
          <div className="player-stats">
            <span>üí™ {character.strength}</span>
            <span>üèÉ {character.endurance}</span>
            <span>‚ö° {character.agility}</span>
          </div>
          <div className="player-body-type">{character.bodyType.toUpperCase()}</div>
        </div>
      </div>
      
      <button className="btn btn-primary start-competition-button bounce" onClick={startCompetition}>
        üöÄ ÎåÄÌöå ÏãúÏûë!
      </button>
    </div>
  );

  const renderCompetingScreen = () => (
    <div className="competition-competing fade-in">
      <h2 className="competing-title">üèÉ‚Äç‚ôÇÔ∏è Í≤ΩÍ∏∞ ÏßÑÌñâÏ§ë...</h2>
      <div className="competing-animation">
        <div className="runner runner-1">üèÉ‚Äç‚ôÇÔ∏è</div>
        <div className="runner runner-2">üèÉ‚Äç‚ôÄÔ∏è</div>
        <div className="runner runner-3">üèÉ‚Äç‚ôÇÔ∏è</div>
      </div>
      <p className="competing-text">ÏÑ†ÏàòÎì§Ïù¥ ÏµúÏÑ†ÏùÑ Îã§ÌïòÍ≥† ÏûàÏäµÎãàÎã§!</p>
    </div>
  );

  const renderResultsScreen = () => (
    <div className="competition-results fade-in">
      <h2 className="results-title">üéâ ÎåÄÌöå Í≤∞Í≥º</h2>
      
      <div className="final-ranking">
        <h3>ÏµúÏ¢Ö ÏàúÏúÑ</h3>
        <div className="ranking-list">
          {competitors.slice(0, 8).map((competitor, index) => (
            <div 
              key={competitor.id} 
              className={`ranking-item ${competitor.isPlayer ? 'player-rank' : ''}`}
            >
              <div className="rank-number">
                {index + 1}
                {index === 0 && 'ü•á'}
                {index === 1 && 'ü•à'}
                {index === 2 && 'ü•â'}
              </div>
              <div className="competitor-info">
                <div className="competitor-name">{competitor.name}</div>
                <div className="competitor-stats">Ï¥ùÌï©: {Math.round(competitor.totalStats)}</div>
              </div>
              <div className="competitor-type">{competitor.bodyType}</div>
            </div>
          ))}
        </div>
      </div>
      
      <div className="player-result">
        <h3>ÎãπÏã†Ïùò Í≤∞Í≥º</h3>
        <div className="result-card">
          <div className="result-rank">
            {playerRank}ÏúÑ / {competitors.length}Î™Ö
          </div>
          <div className="result-prize">{prize}</div>
        </div>
      </div>
      
      <div className="result-buttons">
        <button className="btn btn-success" onClick={onBack}>
          üè† Î©îÏù∏ÏúºÎ°ú
        </button>
        <button className="btn btn-warning" onClick={() => setCompetitionState('intro')}>
          üîÑ Îã§Ïãú ÎèÑÏ†Ñ
        </button>
      </div>
    </div>
  );

  return (
    <div className="competition-screen">
      <button className="btn btn-secondary back-button" onClick={onBack}>
        ‚Üê Îí§Î°úÍ∞ÄÍ∏∞
      </button>
      
      {competitionState === 'intro' && renderIntroScreen()}
      {competitionState === 'competing' && renderCompetingScreen()}
      {competitionState === 'results' && renderResultsScreen()}
    </div>
  );
}
