import React, { useState } from 'react';
import CharacterSprite from './CharacterSprite';
import SVGCharacter from './SVGCharacter';
import CSSCharacter from './CSSCharacter';
import './CharacterDisplay.css';

export default function CharacterDisplay({ character }) {
  const [artStyle, setArtStyle] = useState('css'); // 'pixel', 'sprite', 'svg', 'css'

  const getBodyTypeEmoji = (bodyType) => {
    switch (bodyType) {
      case 'beginner': return '🙂';
      case 'intermediate': return '💪';
      case 'advanced': return '🏋️';
      case 'elite': return '🏆';
      default: return '🙂';
    }
  };

  const renderCharacterArt = () => {
    switch (artStyle) {
      case 'sprite':
        return <CharacterSprite character={character} animated={true} />;
      case 'svg':
        return <SVGCharacter character={character} animated={true} />;
      case 'css':
        return <CSSCharacter character={character} animated={true} />;
      default:
        return renderPixelCharacter();
    }
  };

  const renderPixelCharacter = () => {
    const getBodyTypeColor = (bodyType) => {
      switch (bodyType) {
        case 'beginner': return '#95a5a6';
        case 'intermediate': return '#3498db';
        case 'advanced': return '#e67e22';
        case 'elite': return '#f1c40f';
        default: return '#95a5a6';
      }
    };

    const getBodySize = (bodyType) => {
      switch (bodyType) {
        case 'beginner': return 1;
        case 'intermediate': return 1.1;
        case 'advanced': return 1.2;
        case 'elite': return 1.3;
        default: return 1;
      }
    };

    const bodyColor = getBodyTypeColor(character.bodyType);
    const bodySize = getBodySize(character.bodyType);

    return (
      <div className="pixel-character" style={{ transform: `scale(${bodySize})` }}>
        <div className="pixel-block head" style={{ backgroundColor: '#fdbcb4' }} />
        <div className="pixel-block torso" style={{ backgroundColor: bodyColor }} />
        <div className="pixel-block left-arm" style={{ backgroundColor: bodyColor }} />
        <div className="pixel-block right-arm" style={{ backgroundColor: bodyColor }} />
        <div className="pixel-block left-leg" style={{ backgroundColor: '#34495e' }} />
        <div className="pixel-block right-leg" style={{ backgroundColor: '#34495e' }} />
        <div className="level-text">Lv.{character.level}</div>
      </div>
    );
  };

  return (
    <div className="character-display card">
      <h3>캐릭터</h3>
      
      {/* 아트 스타일 선택 버튼 */}
      <div className="art-style-selector">
        <button 
          className={`style-btn ${artStyle === 'pixel' ? 'active' : ''}`}
          onClick={() => setArtStyle('pixel')}
        >
          픽셀
        </button>
        <button 
          className={`style-btn ${artStyle === 'css' ? 'active' : ''}`}
          onClick={() => setArtStyle('css')}
        >
          CSS
        </button>
        <button 
          className={`style-btn ${artStyle === 'svg' ? 'active' : ''}`}
          onClick={() => setArtStyle('svg')}
        >
          SVG
        </button>
        <button 
          className={`style-btn ${artStyle === 'sprite' ? 'active' : ''}`}
          onClick={() => setArtStyle('sprite')}
        >
          이미지
        </button>
      </div>
      
      <div className="character-container">
        {renderCharacterArt()}
        
        <div className="character-info">
          <div className="body-type">
            {getBodyTypeEmoji(character.bodyType)} {character.bodyType.toUpperCase()}
          </div>
          
          <div className="character-stats">
            <div className="stat-item">
              <span className="stat-label">EXP:</span>
              <span className="stat-value exp">{character.experience}</span>
            </div>
            <div className="stat-item">
              <span className="stat-label">💰</span>
              <span className="stat-value coins">{character.coins}</span>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
